        // Get code from a GitHub repository
        checkout([$class: 'GitSCM', branches: [[name: '*/rick']], doGenerateSubmoduleConfigurations: false, extensions: [[$class: 'CloneOption', noTags: true, reference: '', shallow: true], [$class: 'SubmoduleOption', disableSubmodules: false, parentCredentials: false, recursiveSubmodules: true, reference: '', shallow: true, trackingSubmodules: false]], submoduleCfg: [], userRemoteConfigs: [[url: 'https://github.com/basraven/la1r.git']]])
        sh label: 'create hosts', script: 'printf "[la1r]\n$os $hosts" > ./hosts'
        sh label: 'copy ansible.cfg to /etc/ansible/ansible.cfg', script: 'cp -f ./cicd/ansible/config/ansible.cfg /etc/ansible/ansible.cfg'


        // sh label: 'test ssh', script: 'sshpass -p "vagrant" ssh -o "StrictHostKeyChecking=no"  $default_ssh_user@$hosts \'ls -altr\''
        // sh label: 'ls local', script: 'ls -altr'
    }
    stage('Build') {
        ansiblePlaybook colorized: true, disableHostKeyChecking: true, installation: 'ansible', inventory: 'cicd/ansible/hosts', playbook: 'cicd/ansible/site.yml', tags: 'initial'
    }
